import { Component } from '@angular/core';

@Component({
  selector: 'app-organizer',
  templateUrl: './organizer.component.html',
  styleUrls: ['./organizer.component.css']
})
export class OrganizerComponent {
  days = ['Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes'];
  hours = ['14:15', '15:45', '16:30', '17:15'];

  // Materias disponibles
  subjects = [
    { id: 1, name: 'CONTABILIDAD BÁSICA', group: '623 G:1', day: 'Lunes', time: '14:15' },
    { id: 2, name: 'CONTABILIDAD BÁSICA', group: '693D G:1', day: 'Martes', time: '14:15' },
    { id: 3, name: 'CONTABILIDAD III', group: '684B G:C1', day: 'Jueves', time: '16:30' }
  ];

  selectedSubjects: any[] = [];

  toggleSubject(subject: any, event: any) {
    if (event.target.checked) {
      this.selectedSubjects.push(subject);
    } else {
      this.selectedSubjects = this.selectedSubjects.filter(s => s.id !== subject.id);
    }
  }

  getSubjectsForCell(day: string, hour: string) {
    return this.selectedSubjects.filter(subject => subject.day === day && subject.time === hour);
  }
}
<div class="container">
  <h2>Organizador de Horarios</h2>

  <!-- Panel lateral para seleccionar materias -->
  <div class="sidebar">
    <h3>Materias disponibles</h3>
    <ul>
      <li *ngFor="let subject of subjects">
        <input type="checkbox" (change)="toggleSubject(subject, $event)" />
        {{ subject.name }} ({{ subject.group }}) - {{ subject.day }} {{ subject.time }}
      </li>
    </ul>
  </div>

  <!-- Tabla de horarios -->
  <table class="schedule-table">
    <thead>
      <tr>
        <th>Hora</th>
        <th *ngFor="let day of days">{{ day }}</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let hour of hours">
        <td>{{ hour }}</td>
        <td *ngFor="let day of days">
          <div *ngFor="let subject of getSubjectsForCell(day, hour)" class="subject-box">
            {{ subject.name }}<br />{{ subject.group }}
          </div>
        </td>
      </tr>
    </tbody>
  </table>
</div>
